import React, { useState } from 'react';

function Faq_sectio() {
  const [activeIndex, setActiveIndex] = useState(null);

  const toggleAccordion = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  return (
    <div className="container">
      <h2>Frequently Asked Questions</h2>
      <div className="accordion">
        <div className="accordion-item">
          <button
            id="accordion-button-1"
            aria-expanded={activeIndex === 0 ? 'true' : 'false'}
            onClick={() => toggleAccordion(0)}
          >
            <span className="accordion-title">Why is the moon sometimes out during the day?</span>
            <span className="icon" aria-hidden="true"></span>
          </button>
          <div className={`accordion-content ${activeIndex === 0 ? 'show' : ''}`}>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Elementum sagittis vitae et leo duis ut. Ut tortor pretium viverra suspendisse potenti.</p>
          </div>
        </div>
        <div className="accordion-item">
          <button
            id="accordion-button-2"
            aria-expanded={activeIndex === 1 ? 'true' : 'false'}
            onClick={() => toggleAccordion(1)}
          >
            <span className="accordion-title">Why is the sky blue?</span>
            <span className="icon" aria-hidden="true"></span>
          </button>
          <div className={`accordion-content ${activeIndex === 1 ? 'show' : ''}`}>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Elementum sagittis vitae et leo duis ut. Ut tortor pretium viverra suspendisse potenti.</p>
          </div>
        </div>
        {/* Repeat the same structure for other accordion items */}
      </div>
    </div>
  );
}

export default Faq_sectio;
